import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
// CSSモジュールのインポートを相対パスで指定
import styles from './Hero.module.css';
import heroBg from './hero-bg.jpg';  // 画像をインポート

interface HeroProps {
  onCtaClick?: () => void;
  title?: string;
  subtitle?: string;
  buttonText?: string;
}

// defaultエクスポートを削除し、名前付きエクスポートのみにする
export const Hero: React.FC<HeroProps> = ({
  onCtaClick,
  title = "未来を創る\nエンジニアを募集中",
  subtitle = "私たちと一緒に、テクノロジーで世界をより良くしませんか？",
  buttonText = "募集職種を見る"
}) => {
  const navigate = useNavigate();
  const location = useLocation();

  const handleClick = React.useCallback(() => {
    if (onCtaClick) {
      onCtaClick();
      return;
    }

    if (location.pathname !== '/positions') {
      navigate('/positions');
    }
  }, [onCtaClick, navigate, location]);

  return (
    <section 
      className={styles.hero} 
      style={{
        background: `linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.7)),
          url(${heroBg}) center/cover`
      }}
    >
      <div className={styles.container}>
        <h1 className={styles.title}>
          {title.split('\n').map((line, i) => (
            <React.Fragment key={i}>
              {line}
              {i < title.split('\n').length - 1 && <br />}
            </React.Fragment>
          ))}
        </h1>
        <p className={styles.subtitle}>
          {subtitle}
        </p>
        <button 
          className={styles.ctaButton}
          onClick={handleClick}
        >
          {buttonText}
        </button>
      </div>
    </section>
  );
};
