import React from 'react';
import { 
  createBrowserRouter, 
  RouterProvider,
  createRoutesFromElements,
  Route,
  useNavigate
} from 'react-router-dom';
import { Hero } from './components/Hero/Hero';
import { ErrorBoundary } from './components/ErrorBoundary';

// 仮のコンポーネント定義
const Header = () => <header>Header</header>;
const Benefits = () => <div>Benefits</div>;
const TechStack = () => <div>TechStack</div>;
const Positions = () => <div>Positions</div>;
const Culture = () => <div>Culture</div>;
const Contact = () => <div>Contact</div>;

// Layoutコンポーネントを分離
const Layout = () => {
  const navigate = useNavigate();

  const handleHeroClick = React.useCallback(() => {
    navigate('/positions');
  }, [navigate]);

  return (
    <div className="app">
      <Header />
      <main>
        <Hero onCtaClick={handleHeroClick} />
        <Benefits />
        <TechStack />
        <Positions />
        <Culture />
        <Contact />
      </main>
    </div>
  );
};

const router = createBrowserRouter(
  createRoutesFromElements(
    <Route path="/" element={<Layout />}>
      <Route path="positions" element={<Positions />} />
    </Route>
  )
);

const App: React.FC = () => {
  return (
    <ErrorBoundary>
      <RouterProvider router={router} />
    </ErrorBoundary>
  );
};

export default App;
