import React, { useEffect, useState } from 'react';
import styles from './FAQ.module.css';

const faqData = [
  {
    question: 'ReAliceの特徴を教えてください',
    answer: 'ReAliceは、GitHubと連携したスキル偏差値算出、市場価値に基づく年収予測、発信力レベル可視化など、エンジニアの市場価値を数値化する独自の機能を提供しています。また、専任のキャリアアドバイザーによる無料カウンセリングや、企業とのマッチング精度を高めるAIレコメンデーション機能も特徴です。'
  },
  {
    question: 'スキル偏差値はどのように算出されますか？',
    answer: 'GitHubのコミット履歴、プロジェクト数、スター数、フォロワー数などの定量的なデータを分析し、独自のアルゴリズムで算出しています。また、技術スタックごとの市場価値や、オープンソースへの貢献度なども考慮されます。プライベートリポジトリは非公開設定が可能で、公開したくない情報は除外できます。'
  },
  {
    question: '年収予測の精度はどの程度ですか？',
    answer: 'スキル偏差値、経験年数、技術スタック、市場の需要、直近の転職市場データなど、複数の要因を考慮して算出しています。予測精度は約85%で、定期的にデータを更新して市場の実態に即した予測を提供しています。ただし、企業規模や役割によって実際の年収は変動する可能性があります。'
  },
  {
    question: 'キャリアカウンセリングの内容を詳しく教えてください',
    answer: '経験豊富なキャリアアドバイザーが、あなたのキャリアプランや市場価値の向上について無料でアドバイスします。具体的には、スキルの棚卸し、市場価値の分析、キャリアプランの設計、面接対策、条件交渉のサポートなどを提供します。回数制限はなく、転職後のキャリア相談も可能です。'
  },
  {
    question: '企業からのスカウトはどのように届きますか？',
    answer: 'スキル偏差値とプロフィールを基に、AIがマッチ度の高い企業からスカウトが届きます。通常、登録後1週間程度でスカウトが始まり、GitHubとの連携やプロフィールの充実により、より多くのスカウトを受け取ることができます。スカウトの内容には、具体的な報酬レンジや業務内容が含まれます。また、非公開での求職活動も可能です。'
  },
  {
    question: '選考プロセスはどのような流れですか？',
    answer: 'まず、興味のある企業との面談日程を調整します。面談はオンラインで実施可能です。その後、企業の選考プロセスに進み、一般的に1-2回の面接を経て内定となります。面接対策や条件交渉は専任のキャリアアドバイザーがサポートします。また、内定後の入社時期の調整もお手伝いします。'
  }
];

export const FAQ: React.FC = () => {
  const [openIndex, setOpenIndex] = useState<number | null>(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry, index) => {
          if (entry.isIntersecting) {
            setTimeout(() => {
              entry.target.classList.add(styles.visible);
            }, index * 100);
          }
        });
      },
      {
        threshold: 0.1
      }
    );

    const items = document.querySelectorAll(`.${styles.faqItem}`);
    items.forEach((item) => observer.observe(item));

    return () => observer.disconnect();
  }, []);

  const toggleAnswer = (index: number) => {
    setOpenIndex(openIndex === index ? null : index);
  };

  return (
    <section className={styles.faqSection}>
      <div className={styles.container}>
        <h2 className={styles.title}>よくある質問</h2>
        <div className={styles.faqList}>
          {faqData.map((faq, index) => (
            <div 
              key={index} 
              className={styles.faqItem}
            >
              <div 
                className={styles.question} 
                onClick={() => toggleAnswer(index)}
              >
                <span>{faq.question}</span>
                <span className={`${styles.icon} ${openIndex === index ? styles.open : ''}`}>
                  ▼
                </span>
              </div>
              <div className={`${styles.answer} ${openIndex === index ? styles.open : ''}`}>
                {faq.answer}
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};
