import React from 'react';
import styles from './Hero.module.css';

interface HeroProps {
  onCtaClick?: () => void;
  title?: string;
  subtitle?: string;
  buttonText?: string;
}

export const Hero: React.FC<HeroProps> = ({
  onCtaClick,
  title = "技術力で転職したい\nIT/WEBエンジニアへ",
  subtitle = "「Findy」はハイスキルなエンジニアと企業をマッチする転職サービスです。\nGitHubの開発履歴をもとにスキルと適正年収を可視化します。",
  buttonText = "GitHubで新規登録"
}) => {
  const handleClick = React.useCallback(() => {
    if (onCtaClick) {
      onCtaClick();
    }
  }, [onCtaClick]);

  return (
    <section className={styles.hero}>
      <div className={styles.container}>
        <div className={styles.topContent}>
          <img 
            src="/worried-engineer.png" 
            alt="悩むエンジニア" 
            className={styles.worryImage} 
          />
          <h2 className={styles.worryTitle}>
            こんなことに<span className={styles.highlight}>悩んで</span>いませんか？
          </h2>
          <ul className={styles.worryList}>
            <li>転職したいけど、自分の技術力を評価してくれる会社が分からない</li>
            <li>自分の希望年収に見合う会社を見つけたい</li>
          </ul>
        </div>

        <div className={styles.featureCards}>
          <div className={styles.featureCard}>
            <h3>スキル偏差値とは？</h3>
            <div className={styles.skillChart}>
              {/* スキルチャートの画像 */}
              <img src="/skill-chart.png" alt="スキル偏差値チャート" />
            </div>
            <ul className={styles.featureList}>
              <li>GitHub連携でスキルを偏差値化</li>
              <li>開発言語ごとの偏差値も分かる</li>
              <li>OSS活動をするほど偏差値が上がる</li>
            </ul>
            <button className={styles.featureButton}>
              GitHub連携でスキル偏差値を見る
            </button>
          </div>

          <div className={styles.featureCard}>
            <h3>年収予測機能とは？</h3>
            <div className={styles.salaryRange}>
              {/* 年収予測の画像 */}
              <img src="/salary-range.png" alt="年収予測範囲" />
            </div>
            <ul className={styles.featureList}>
              <li>スキル偏差値と連動し想定年収を算出</li>
              <li>職種や経験年数なども年収予測に加味</li>
              <li>自分の市場価値が分かる</li>
            </ul>
            <button className={styles.featureButton}>
              GitHub連携で年収予測を見る
            </button>
          </div>
        </div>
      </div>
    </section>
  );
};
