import React from 'react';
import styles from './Overview.module.css';

export const Overview: React.FC = () => {
  const scrollToSection = (sectionId: string) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section className={styles.overviewSection}>
      <div className={styles.container}>
        <h2 className={styles.title}>概要</h2>

        <div className={styles.content}>
          <div className={styles.sections}>
            <h3>セクション</h3>
            <ul className={styles.sectionList}>
              <li onClick={() => scrollToSection('hero')}>トップ</li>
              <li onClick={() => scrollToSection('companies')}>取引企業</li>
              <li onClick={() => scrollToSection('benefits')}>特徴</li>
              <li onClick={() => scrollToSection('steps')}>利用の流れ</li>
              <li onClick={() => scrollToSection('jobExamples')}>転職事例</li>
              <li onClick={() => scrollToSection('interviews')}>社内インタビュー</li>
              <li onClick={() => scrollToSection('newsBlog')}>ニュース＆ブログ</li>
              <li onClick={() => scrollToSection('faq')}>よくある質問</li>
              <li onClick={() => scrollToSection('applicationForm')}>応募フォーム</li>
            </ul>
          </div>

          <div className={styles.policies}>
            <h3>ポリシー</h3>
            <ul className={styles.policyList}>
              <li><a href="#">利用規約</a></li>
              <li><a href="#">個人情報の取り扱いについて</a></li>
              <li><a href="#">個人情報保護方針</a></li>
            </ul>
          </div>

          <div className={styles.company}>
            <div className={styles.logo}>ReAlice</div>
            <div className={styles.companyName}>ReAlice Inc.</div>
          </div>
        </div>
      </div>
    </section>
  );
};
