import React from 'react';
import { 
  BrowserRouter as Router,
  Routes,
  Route
} from 'react-router-dom';
import { Hero } from './components/Hero/Hero';
import { ErrorBoundary } from './components/ErrorBoundary';

// 仮のコンポーネント定義
const Header = () => <header style={{ padding: '20px', backgroundColor: '#f5f5f5' }}>Header</header>;
const Benefits = () => <div style={{ padding: '20px' }}>Benefits</div>;
const TechStack = () => <div style={{ padding: '20px' }}>TechStack</div>;
const Positions = () => <div style={{ padding: '20px' }}>Positions</div>;
const Culture = () => <div style={{ padding: '20px' }}>Culture</div>;
const Contact = () => <div style={{ padding: '20px' }}>Contact</div>;

const App: React.FC = () => {
  const handleHeroClick = () => {
    console.log('Hero clicked');
    // ナビゲーションは後で実装
  };

  return (
    <ErrorBoundary>
      <Router>
        <div className="app">
          <Header />
          <main>
            <Routes>
              <Route path="/" element={
                <>
                  <Hero onCtaClick={handleHeroClick} />
                  <Benefits />
                  <TechStack />
                  <Culture />
                  <Contact />
                </>
              } />
              <Route path="/positions" element={<Positions />} />
            </Routes>
          </main>
        </div>
      </Router>
    </ErrorBoundary>
  );
};

export default App;
